import React, { FC, useState, useEffect } from 'react';
import { PcaWrapper } from './pca.styled';
import { PaginationControl } from 'react-bootstrap-pagination-control';
import CardCar from '../CardCar/CardCar';
import standartCarImg from '../../img/standartcar.jpg';

interface CarProps {
  car: {
    comments: string[];
    digits: string;
    is_stolen: boolean;
    model: string;
    model_year: number;
    operations: Array<any>;
    photo_url: string;
    region: {
      name: string;
      name_ua: string;
      slug: string;
      old_code: string;
      new_code: string;
    };
    stolen_details: any;
    vendor: string;
    vin: string;
  } | null | string;
  nomer?: string;
}

const Pca: FC<CarProps> = ({ car, nomer = "" }) => {
  const [page, setPage] = useState(1);
  const [cars, setCars] = useState<any[]>([]);
  const itemsPerPage = 20;

  useEffect(() => {
    if (car != null && typeof car === 'object') {
      setCars(car.operations);
    }
  }, [car]);

  const indexOfLastItem = page * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentItems = cars.slice(indexOfFirstItem, indexOfLastItem);

  return (
    <PcaWrapper>
      {currentItems.map((operation, index) => (
        <CardCar
          key={index}
          num={nomer}
          img={car && typeof car === 'object' ? car.photo_url || standartCarImg : standartCarImg}
          wanted={car && typeof car === 'object' ? car.is_stolen : false}
          brand={car && typeof car === 'object' ? car.vendor : ''}
          model={car && typeof car === 'object' ? car.model : ''}
          modelyear={car && typeof car === 'object' ? car.model_year.toString() : ''}
          region={car && typeof car === 'object' ? car.region.name : ''}
          datereg={operation.date || ''}
          color={typeof operation.color === 'object' ? operation.color.ua : operation.color || ''}
          fuel={typeof operation.fuel === 'object' ? operation.fuel.ua : operation.fuel || ''}
        />
      ))}

      <PaginationControl
        page={page}
        between={4}
        total={cars.length}
        limit={itemsPerPage}
        changePage={(page) => {
          setPage(page);
        }}
        ellipsis={1}
      />
    </PcaWrapper>
  );
};

export default Pca;
