import React, { FC, useState, useEffect } from 'react';
import './App.css';
import axios from "axios";
import CardCar from './components/CardCar/CardCar';
import 'bootstrap/dist/css/bootstrap.min.css'; // Импортируем CSS Bootstrap

import search from './components/searchnum/searchnum';
import ButtonComponent from './components/ButtonMap/ButtonMap'; // Импортируем ButtonComponent

interface Car {
  comments: string[];
  digits: string;
  is_stolen: boolean;
  model: string;
  model_year: number;
  operations: Array<any>;
  photo_url: string;
  region: {
    name: string;
    name_ua: string;
    slug: string;
    old_code: string;
    new_code: string;
  };
  stolen_details: any;
  vendor: string;
  vin: string;
}

const numer = 'АЕ7777АЕ';

function App() {
  const url = `https://baza-gai.com.ua/nomer/${numer}`;
  const key = "dcb856e75162ec68a57c4e5f54f430ca";

  const [car, setCar] = useState<Car | null>(null);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    axios.get(url, {
      headers: {
        "Accept": "application/json",
        "X-Api-Key": key
      }
    })
    .then(response => {
      setCar(response.data);
      console.log(response.data);
      setLoading(false);
    })
    .catch(err => {
      setError("Произошла ошибка при загрузке данных");
      setLoading(false);
    });
  }, []);

  const handleButtonClick = () => {
    // Добавьте функциональность для кнопки
    console.log("Кнопка нажата!");
  };

  if (loading) {
    return <div>Загрузка...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  if (!car) {
    return <div>Нет данных о машинах</div>;
  }

  return (
    <div className="App">
      <div className="background"></div>
      <search></search>
      <CardCar
        num={numer}
        vin={car.vin.toString()}
        img={car.photo_url}
        wanted={car.is_stolen}
        brand={car.vendor}
        model={car.model}
        modelyear={car.model_year.toString()}
        region={car.region.name}
      />
      <ButtonComponent onClick={handleButtonClick} />
    </div>
  );
}

export default App;
