import React, { FC, useEffect, useState } from 'react';
import { PaginationControlContainer, PcaWrapper, CardCarWrapper } from './pca.styled';
import Pagination from 'react-bootstrap/Pagination';
import CardCar from '../CardCar/CardCar';
import { PaginationControl } from 'react-bootstrap-pagination-control';
import standartCarImg from '../../img/standartcar.jpg';

interface CarProps {
  car: {
    comments: string[];
    digits: string;
    is_stolen: boolean;
    model: string;
    model_year: number;
    operations: Array<any>;
    photo_url: string;
    region: {
      name: string;
      name_ua: string;
      slug: string;
      old_code: string;
      new_code: string;
    };
    stolen_details: any;
    vendor: string;
    vin: string;
  } | null | string;
  nomer?: string; 
}

const getCarImageUrl = async (make = "", model = ""): Promise<string> => {
  const apiKey = 'dcb856e75162ec68a57c4e5f54f430ca'; // Замените на ваш API ключ
  const baseUrl = 'https://baza-gai.com.ua';

  try {
    // Сначала получаем информацию о модели
    const modelResponse = await fetch(`${baseUrl}/make/${make}/${model}`, {
      headers: {
        'Accept': 'application/json',
        'X-Api-Key': apiKey
      }
    });

    if (!modelResponse.ok) {
      throw new Error('Network response was not ok');
    }

    const modelData = await modelResponse.json();

    // Проверяем, есть ли фото в данных модели
    if (modelData.catalog_model && modelData.catalog_model.photo_url) {
      return modelData.catalog_model.photo_url;
    }

    // Если фото нет в данных модели, пробуем получить фото из каталога
    const catalogResponse = await fetch(`${baseUrl}/catalog/${make}/${model}/photos`, {
      headers: {
        'Accept': 'application/json',
        'X-Api-Key': apiKey
      }
    });

    if (!catalogResponse.ok) {
      throw new Error('Network response was not ok');
    }

    const catalogData = await catalogResponse.json();

    if (catalogData.generations && catalogData.generations.length > 0 &&
        catalogData.generations[0].bodies && catalogData.generations[0].bodies.length > 0 &&
        catalogData.generations[0].bodies[0].photos && catalogData.generations[0].bodies[0].photos.length > 0) {
      return catalogData.generations[0].bodies[0].photos[0].img_path;
    }

    return "";
  } catch (error) {
    console.error('Error fetching car image:', error);
    return "";
  }
}

const Pca: FC<CarProps> = ({ car, nomer = "" }) => {
  const cars = [];

  const [page, setPage] = useState(1);
  const [animationClass, setAnimationClass] = useState('slide-in-right');
  const [imgUrls, setImgUrls] = useState<string[]>([]);

  if (car != null && typeof car === 'object') {
    var car1image = car.photo_url;
    var standartCar = standartCarImg;
    for (let i = 0; i < car.operations.length; i++) {
      cars.push(car.operations[i]);
    }
  }

  const itemsPerPage = 1;
  const totalPages = Math.ceil(cars.length / itemsPerPage);
  const startIndex = (page - 1) * itemsPerPage;
  const endIndex = startIndex + itemsPerPage;
  const currentPageItems = cars.slice(startIndex, endIndex);

  useEffect(() => {
    const fetchImages = async () => {
      const urls = await Promise.all(
        cars.map(async (item) => {
          if (page === 1) {
            return car1image;
          } else {
            const url = await getCarImageUrl(item.vendor, item.model);
            return url || '';
          }
        })
      );
      setImgUrls(urls);
    };
    fetchImages();
  }, [page, car]);

  const changePage = (newPage: number) => {
    const direction = newPage > page ? 'right' : 'left';
    setAnimationClass(`slide-out-${direction}`);
    setTimeout(() => {
      setPage(newPage);
      setAnimationClass(`slide-in-${direction}`);
    }, 500); 
  };

  return (
    <div>
      <PcaWrapper>
        {currentPageItems.map((item, index) => (
          <CardCarWrapper
            key={index}
            className={animationClass}
            onAnimationEnd={() => setAnimationClass('')}
          >
            <CardCar
              num={nomer}
              img={imgUrls[startIndex + index] || standartCarImg}
              wanted={false}
              brand={item.vendor}
              model={item.model}
              modelyear={item.model_year}
              region={item.address}
              datereg={item.registered_at}
              color={item.color.ru}
              fuel={item.fuel.ru}
            />
          </CardCarWrapper>
        ))}
      </PcaWrapper>
      <PaginationControlContainer>
        <PaginationControl
          page={page}
          between={4}
          total={cars.length}
          limit={itemsPerPage}
          changePage={changePage}
          ellipsis={1}
        />
      </PaginationControlContainer>
    </div>
  );
};

export default Pca;
