import styled from 'styled-components';

export const PcaWrapper = styled.div`
  display: flex; /* Или grid, в зависимости от желаемого расположения */
  gap: 1rem; /* Расстояние между карточками */
  opacity: 0; /* Начальное состояние - невидимые карточки */
  transition: opacity 0.5s ease-in-out; /* Плавный переход прозрачности */
  .PcaWrapper.page-enter-active, /* Карточки появляются */
  .PcaWrapper.page-exit-active { /* Карточки исчезают */
  opacity: 1;
}
`;

export const PaginationControlContainer = styled.div`
margin-left: 10px;
`
