import React, { FC, useState, useEffect } from 'react';
import './App.css';
import axios from "axios";
import CardCar from './components/CardCar/CardCar';

interface Car {
  comments: string[];
  digits: string;
  is_stolen: boolean;
  model: string;
  model_year: number;
  operations: Array<any>;
  photo_url: string;
  region: {
    name: string;
    name_ua: string;
    slug: string;
    old_code: string;
    new_code: string;
  };
  stolen_details: any;
  vendor: string;
  vin: string;
}

interface Cars extends Array<Car> {}

function App() {
  const url = "https://baza-gai.com.ua/nomer/KA0007XB";
  const key = "dcb856e75162ec68a57c4e5f54f430ca";

  const [cars, setCars] = useState<Cars | null>(null);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    axios.get(url, {
      headers: {
        "Accept": "application/json",
        "X-Api-Key": key
      }
    })
    .then(response => {
      setCars(response.data);
      setLoading(false);
    })
    .catch(err => {
      setError("An error occurred while fetching data");
      setLoading(false);
    });
  }, []);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  if (!cars || cars.length === 0) {
    return <div>No Cars data available</div>;
  }


  console.log(cars);
  const car = cars[0];

  return (
    <div className="App">
<CardCar
          vin={car.vin}
          img={car.photo_url}
          wanted={car.is_stolen}
          brand={car.vendor}
          model={car.model}
          modelyear={car.model_year.toString()}
          region={car.region.toString()}
        />
    </div>
  );
}

export default App;
