import React, { FC, useState, useEffect } from 'react';
import './App.css';
import axios from "axios";
import CardCar from './components/CardCar/CardCar';
import Search from './components/searchnum/searchnum';

interface Car {
  comments: string[];
  digits: string;
  is_stolen: boolean;
  model: string;
  model_year: number;
  operations: Array<any>;
  photo_url: string;
  region: {
    name: string;
    name_ua: string;
    slug: string;
    old_code: string;
    new_code: string;
  };
  stolen_details: any;
  vendor: string;
  vin: string;
}


function App() {
  const [car, setCar] = useState<Car | null>(null);
  const [loading, setLoading] = useState<boolean>(false);
  const [error, setError] = useState<string | null>(null);
  const [searchNumber, setSearchNumber] = useState<string>('');

  const key = "dcb856e75162ec68a57c4e5f54f430ca";

  const handleSearch = (number: string) => {
    setSearchNumber(number);
    setLoading(true);
    setError(null);

    const url = `https://baza-gai.com.ua/nomer/${number}`;

    axios.get(url, {
      headers: {
        "Accept": "application/json",
        "X-Api-Key": key
      }
    })
    .then(response => {
      setCar(response.data);
      setLoading(false);
    })
    .catch(err => {
      setError("An error occurred while fetching data");
      setLoading(false);
    });
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div className="App">
      <div className="background"></div>
      <Search onSearch={handleSearch} />
      {car && (
        <CardCar
          num={searchNumber}
          vin={car.vin.toString()}
          img={car.photo_url}
          wanted={car.is_stolen}
          brand={car.vendor}
          model={car.model}
          modelyear={car.model_year.toString()}
          region={car.region.name}
        />
      )}
    </div>
  );
}

export default App;
