import P from 'react-pagimagic';
import users from './components/user.json';

const styles = {
  fontFamily: 'sans-serif',
  textAlign: 'center',
};

const App = () => {
  const renderChildren = list => {
    return list.map(item => {
      return (
        <div key={item.name}>
          {item.name}
        </div>
      );
    });
  };

  const CURRENT_PAGE_INDEX = 0;

  return (
    <div style={styles}>
      <h3>Maximum visible 5</h3>
      <P
        list={users}
        itemsPerPage={3}
        currentPageIndex={CURRENT_PAGE_INDEX}
        className="your-class-if-its-necessary"
        maximumVisiblePaginators={5}
        renderChildren={renderChildren}
        useDefaultStyles
        showCounter
      />

      <hr />

      <h3>Maximum visible 7</h3>
      <P
        list={users}
        itemsPerPage={3}
        currentPageIndex={CURRENT_PAGE_INDEX}
        className="your-class-if-its-necessary"
        maximumVisiblePaginators={7}
        renderChildren={renderChildren}
        useDefaultStyles
      />

      <hr/>

      <h3>Maximum visible 9</h3>
      <P list={users}
        itemsPerPage={3}
        currentPageIndex={CURRENT_PAGE_INDEX}
        className="your-class-if-its-necessary"
        maximumVisiblePaginators={9}
        renderChildren={renderChildren}
        useDefaultStyles
      />
    </div>
  )
}

export default App;
