import React, { FC, useState, useMemo } from 'react';
import { PcaWrapper } from './pca.styled';
import CardCar from '../CardCar/CardCar';
import { PaginationControl } from 'react-bootstrap-pagination-control';
import standartCarImg from '../../img/standartcar.jpg';

interface CarProps {
  car: {
    comments: string[];
    digits: string;
    is_stolen: boolean;
    model: string;
    model_year: number;
    operations: Array<any>;
    photo_url: string;
    region: {
      name: string;
      name_ua: string;
      slug: string;
      old_code: string;
      new_code: string;
    };
    stolen_details: any;
    vendor: string;
    vin: string;
  } | null | string;
  nomer?: string;
}

const Pca: FC<CarProps> = ({ car, nomer = "" }) => {
  const [page, setPage] = useState(1);
  const itemsPerPage = 1; // Количество элементов на странице

  const cars = useMemo(() => {
    if (car != null && typeof car === 'object') {
      return car.operations;
    }
    return [];
  }, [car]);

  const car1image = car && typeof car === 'object' ? car.photo_url : undefined;
  const standartCar = standartCarImg;

  // Вычисляем общее количество страниц
  const totalPages = Math.ceil(cars.length / itemsPerPage);

  // Получаем элементы для текущей страницы
  const currentPageItems = useMemo(() => {
    const startIndex = (page - 1) * itemsPerPage;
    const endIndex = startIndex + itemsPerPage;
    return cars.slice(startIndex, endIndex);
  }, [cars, page, itemsPerPage]);

  return (
    <PcaWrapper>
      {currentPageItems.map((item, index) => (
        <CardCar
          key={index}
          num={nomer}
          img={index === 0 && page === 1 ? car1image || standartCar : standartCar}
          wanted={Boolean(car && typeof car === 'object' && car.is_stolen)}
          brand={item.vendor || (car && typeof car === 'object' ? car.vendor : '')}
          model={item.model || (car && typeof car === 'object' ? car.model : '')}
          modelyear={item.model_year || (car && typeof car === 'object' ? car.model_year : '').toString()}
          region={item.address || (car && typeof car === 'object' && car.region ? car.region.name : '')}
          datereg={item.registered_at || ''}
          color={(item.color && item.color.ru) || ''}
          fuel={(item.fuel && item.fuel.ru) || ''}
        />
      ))}
      <PaginationControl
        page={page}
        between={4}
        total={cars.length}
        limit={itemsPerPage}
        changePage={(newPage) => {
          setPage(newPage);
        }}
        ellipsis={1}
      />
    </PcaWrapper>
  );
};

export default Pca;
