// comparison.tsx
import React, { FC, useState, useEffect } from 'react';
import { Modal, ModalContent, CloseButton } from './comparison.styled';
import Search from '../searchnum/searchnum';

interface ComparisonProps {
  onClose: () => void;
}

interface Car {
  comments: string[];
  digits: string;
  is_stolen: boolean;
  model: string;
  model_year: number;
  operations: Array<any>;
  photo_url: string;
  region: {
    name: string;
    name_ua: string;
    slug: string;
    old_code: string;
    new_code: string;
  };
  stolen_details: any;
  vendor: string;
  vin: string;
}

const Comparison: FC<ComparisonProps> = ({ onClose }) => {
  const [carData, setCarData] = useState<Car | null | string>(null);
  const [searchNomer, setSearchNomer] = useState<string>("");
  const [loading, setLoading] = useState<boolean>(false);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    if (searchNomer === "") return; // Avoid fetching if searchNomer is empty

    const fetchCarData = async () => {
      setLoading(true);
      setError(null); // Clear previous errors

      const url = `https://baza-gai.com.ua/nomer/${searchNomer}`;
      const key = "9c16e05f3ed9a9d34bda42abd1fd91d5";

      try {
        const response = await fetch(url, { headers: { "Accept": "application/json", "X-Api-Key": key } });
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        setCarData(data);
      } catch (error) {
        setError('Error fetching data');
        console.error("Error fetching car data:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchCarData();
  }, [searchNomer]); // Fetch new data when searchNomer changes

  const handleSearch = (nomer: string) => {
    setSearchNomer(nomer);
  };

  return (
    <Modal>
      <CloseButton onClick={onClose}>Закрыть</CloseButton>
      <ModalContent>
        <Search setDataFromSearch={setCarData} setNomer={handleSearch} />
        {loading && <div>Loading...</div>}
        {error && <div>{error}</div>}
        {carData && (
          <div>
            {/* Render carData here */}
            <p>{(carData as Car).model}</p>
            <p>{(carData as Car).vin}</p>
            {/* Add other fields as necessary */}
          </div>
        )}
      </ModalContent>
    </Modal>
  );
};

export default Comparison;
