import React, { useState, useEffect } from 'react';
import './App.css';
import axios from "axios";
import CardCar from './components/CardCar/CardCar';
import Search from './components/searchnum/searchnum';

interface Car {
  comments: string[];
  digits: string;
  is_stolen: boolean;
  model: string;
  model_year: number;
  operations: Array<any>;
  photo_url: string;
  region: {
    name: string;
    name_ua: string;
    slug: string;
    old_code: string;
    new_code: string;
  };
  stolen_details: any;
  vendor: string;
  vin: string;
}

const numer = 'АЕ7777АЕ';

function getnumApi(dataFromSearch: string | null): Car | null {
  const [car, setCar] = useState<Car | null>(null);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    if (!dataFromSearch) return; // Добавляем проверку на dataFromSearch

    const url = `https://baza-gai.com.ua/nomer/${dataFromSearch}`;
    const key = "dcb856e75162ec68a57c4e5f54f430ca";

    axios.get(url, {
      headers: {
        "Accept": "application/json",
        "X-Api-Key": key
      }
    })
    .then(response => {
      setCar(response.data);
      setLoading(false);
    })
    .catch(err => {
      setError("An error occurred while fetching data");
      setLoading(false);
    });
  }, [dataFromSearch]);

  if (loading) {
    return null; // Возвращаем null вместо загрузочного элемента
  }

  if (error) {
    return null; // Возвращаем null в случае ошибки
  }

  return car; // Возвращаем данные автомобиля или null, если данных нет
}

function App() {
  const [dataFromSearch, setDataFromSearch] = useState<string | null>(null); // Указываем тип данных для dataFromSearch

  const car = getnumApi(dataFromSearch);

  if (!car) {
    return (
      <div className="App">
        <div className="background"></div>
        <Search setDataFromSearch={setDataFromSearch} />
        <div>No car data available</div>
      </div>
    );
  }

  return (
    <div className="App">
      <div className="background"></div>
      <Search setDataFromSearch={setDataFromSearch} />

      <CardCar
        num={numer}
        vin={car.vin.toString()}
        img={car.photo_url}
        wanted={car.is_stolen}
        brand={car.vendor}
        model={car.model}
        modelyear={car.model_year.toString()}
        region={car.region.name}
      />
    </div>
  );
}

export default App;
