// src/components/SvgMap/SvgMap.tsx
import React from 'react';
import { ReactSVG } from 'react-svg';
import { SvgContainer, Modal, CloseButton } from './SVGMap.styled';
import Map from './ukraine.svg'; // Убедитесь, что этот путь верный

interface SvgMapProps {
  isOpen: boolean;
  onClose: () => void;
}

const SvgMap: React.FC<SvgMapProps> = ({ isOpen, onClose }) => {
  if (!isOpen) return null; // Не рендерим, если модальное окно закрыто

  return (
    <Modal>
      <CloseButton onClick={onClose}>Закрыть</CloseButton>
      <SvgContainer>
        {/* Логирование для проверки правильности импорта SVG */}
        <ReactSVG
          src={Map}
          beforeInjection={(svg) => {
            console.log('SVG загружен', svg);
            // Необязательно: можно изменить свойства SVG здесь
          }}
          onError={(error) => {
            console.error('Ошибка при загрузке SVG:', error);
          }}
        />
      </SvgContainer>
    </Modal>
  );
};

export default SvgMap;
