import React, { FC, useState, useEffect } from 'react';
import './App.css';
import axios from "axios";
import CardCar from './components/CardCar/CardCar';
import Search from './components/searchnum/searchnum';

interface Car {
  comments: string[];
  digits: string;
  is_stolen: boolean;
  model: string;
  model_year: number;
  operations: Array<any>;
  photo_url: string;
  region: {
    name: string;
    name_ua: string;
    slug: string;
    old_code: string;
    new_code: string;
  };
  stolen_details: any;
  vendor: string;
  vin: string;
}

function App() {
  const key = "dcb856e75162ec68a57c4e5f54f430ca";

  const [car, setCar] = useState<Car | null>(null);
  const [loading, setLoading] = useState<boolean>(false);
  const [error, setError] = useState<string | null>(null);
  const [searchNumber, setSearchNumber] = useState<string>('');

  const handleNum = (searchNumber: string) => {
    setSearchNumber(searchNumber);
  }

  useEffect(() => {
    if (searchNumber) {
      setLoading(true);
      setError(null); 
      const url = `https://baza-gai.com.ua/nomer/${searchNumber}`;
      axios.get(url, {
        headers: {
          "Accept": "application/json",
          "X-Api-Key": key
        }
      })
      .then(response => {
        if (response.data) {
          setCar(response.data);
          console.log("API Response Data:", response.data);
        } else {
          setCar(null);
          setError("No car data found for the given number");
        }
        setLoading(false);
      })
      .catch(err => {
        console.error("API Error:", err);
        setError("An error occurred while fetching data");
        setLoading(false);
      });
    }
  }, [searchNumber]);

  const isValidCar = (car: any): car is Car => {
    return car && car.vin && car.photo_url && car.vendor && car.model && car.model_year && car.region;
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    <div className="App">
      <div className="background"></div>
      <Search onSearch={handleNum} />
      {isValidCar(car) ? (
        <CardCar
          num={error}
          vin={error}
          img={error}
          wanted={false}
          brand={error}
          model={error}
          modelyear={error}
          region={error}
        />
      ) : (
        <div>No valid car data available</div>
      )}
    </div>
  }

  return (
    <div className="App">
      <div className="background"></div>
      <Search onSearch={handleNum} />
      {isValidCar(car) ? (
        <CardCar
          num={searchNumber}
          vin={car.vin.toString()}
          img={car.photo_url}
          wanted={car.is_stolen}
          brand={car.vendor}
          model={car.model}
          modelyear={car.model_year.toString()}
          region={car.region.name}
        />
      ) : (
        <div>No valid car data available</div>
      )}
    </div>
  );
}

export default App;
