import React from 'react';
import { ReactSVG } from 'react-svg';
import { SvgContainer, Modal, CloseButton } from './SVGMap.styled';
import Map from './ukraine.svg';

interface Region {
  id: string;
  color: string;
}

interface SvgMapProps {
  isOpen: boolean;
  onClose: () => void;
  regions: Region[];
}

const SvgMap: React.FC<SvgMapProps> = ({ isOpen, onClose, regions }) => {
  if (!isOpen) return null;

  // Создаем уникальный ключ для принудительного обновления SVG
  const svgKey = `svg-${Date.now()}`;

  return (
    <Modal>
      <CloseButton onClick={onClose}>Закрыть</CloseButton>
      <SvgContainer>
        <ReactSVG
          key={svgKey} // Уникальный ключ для перезагрузки
          src={Map}
          beforeInjection={(svg) => {
            console.log('SVG загружен', svg);

            // Выводим содержимое regions в консоль
            console.log('Regions in SvgMap:', regions);

            // Применяем цвета к регионам
            regions.forEach((region) => {
              const element = svg.querySelector(`#${region.id}`);
              if (element) {
                element.setAttribute('style', `fill: ${region.color};`);
              }
            });
          }}
          onError={(error) => {
            console.error('Ошибка при загрузке SVG:', error);
          }}
          style={{ width: '100%', height: 'auto' }} // Устанавливаем стили для SVG
        />
      </SvgContainer>
    </Modal>
  );
};

export default SvgMap;
