import React, {useState,useEffect} from 'react';
import { SearchNumContaner, ButtonContainer } from './searchmm.styled';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import './searchmm.css';
import axios from "axios";
import Spinner from 'react-bootstrap/Spinner';
import Alert from 'react-bootstrap/Alert';



interface ChildComponentProps {
  setDataFromSearch: React.Dispatch<React.SetStateAction<Car | null | string>>;
  // setVin: React.Dispatch<React.SetStateAction<string>>;

}




interface Car {
  comments: string[];
  digits: string;
  is_stolen: boolean;
  model: string;
  model_year: number;
  operations: Array<any>;
  photo_url: string;
  region: {
    name: string;
    name_ua: string;
    slug: string;
    old_code: string;
    new_code: string;
  };
  stolen_details: any;
  vendor: string;
  vin: string;
}


const SearchComponent: React.FC<ChildComponentProps> = ({ setDataFromSearch }) => {

  const [Make, setMake] = useState<string>(' ');
  const [Model, setModel] = useState<string>(' ');

  const [carDataFromApi, setcarDataFromApi] = useState<string | null>(' ');


  const getApiCarData = async ( makeapi:string, markapi:string ) => {

    try{
      // let url = `https://baza-gai.com.ua/make/${makeapi.toLowerCase()}/${markapi.toLowerCase()}`;
      // let key = "9c16e05f3ed9a9d34bda42abd1fd91d5";
      // let request = fetch(url, {headers: {"Accept": "application/json", "X-Api-Key": key}}).then(r => r.json());
      // let data = await request;
      // if (data.error) {
      //   setDataFromSearch(null);
      //   setcarDataFromApi(null);
      // } else {

      //   console.log(data);
      //   // setDataFromSearch(null);
      //   // setcarDataFromApi(null);

      //   setDataFromSearch(data);
      //   setcarDataFromApi(data);
      // }

      
      var config = {
      method: "get",
      url: `https://api.vehicledatabases.com/electric-vehicle/2024/${makeapi.toLowerCase()}/${markapi.toLowerCase()}`,
      headers: {
      "x-AuthKey": "{X-AUTHKEY}",
      },
      };
      
      axios(config)
      .then(function (response) {
      console.log(JSON.stringify(response.data));
      })
      .catch(function (error) {
      console.log(error);
      });
    }
    catch{
      console.error();
      setDataFromSearch(null);
      setcarDataFromApi(null);
    };
  }

    
  return (
    <SearchNumContaner>
      <Form.Label htmlFor="inputPassword5" id="LabelEnterNum">Введите марку автомобиля</Form.Label>
      <div className='containerSearch'>
        <Form.Control type="text" id="inputPassword5" aria-describedby="passwordHelpBlock" onChange={ e => setMake(e.target.value)} />
        </div>
     

    <div className='searchContainer'>
      <Form.Label htmlFor="inputPassword5" id="LabelEnterNum">Введите модель автомбобиля</Form.Label>
      <div className='containerSearch'>
        <Form.Control type="text" id="inputPassword5" aria-describedby="passwordHelpBlock" onChange={ e => setModel(e.target.value)} />
        </div>
      <Form.Text id="passwordHelpBlock" className="passwordHelp">
          Убедитесь что данный автомобиль существет
      </Form.Text>
      </div>
      <ButtonContainer>
      <Button variant="success" onClick={() => { getApiCarData(Make, Model)}}>Поиск</Button>
      </ButtonContainer>

    

      {/* {carDataFromApi === null  && 
        <Alert variant="danger" dismissible>
        <Alert.Heading>Ошибка АРІ запроса</Alert.Heading>
        <p className='pinError'>
          Проверте правильность введеных данных или
          повторите запрос. Возможно данный номер не существует.
        </p>
      </Alert>
      } */}
    </SearchNumContaner>

    


  );
};

export default SearchComponent;
