import React, { FC, useState } from 'react';
import { PcaWrapper } from './pca.styled';
import Pagination  from 'react-bootstrap/Pagination';
import CardCar from '../CardCar/CardCar';
import { PaginationControl } from 'react-bootstrap-pagination-control';
import standartCarImg from '../../img/standartcar.jpg'


interface CarProps {
  car: {
    comments: string[];
    digits: string;
    is_stolen: boolean;
    model: string;
    model_year: number;
    operations: Array<any>;
    photo_url: string;
    region: {
      name: string;
      name_ua: string;
      slug: string;
      old_code: string;
      new_code: string;
    };
    stolen_details: any;
    vendor: string;
    vin: string;
  } | null | string;
  nomer?: string; 
}

const Pca: FC<CarProps> = ({ car, nomer = "" }) => {
  const cars = [];


  if (car != null && typeof car === 'object')
  {
    var car1image = car.photo_url;
    var standartCar = standartCarImg;
    for (let i = 0; i<car.operations.length; i++){
      cars.push(car.operations[i]);
    }
  }

  console.log(cars);

  const itemsPerPage = 1;
  const [page, setPage] = useState(1);
  const totalPages = Math.ceil(cars.length / itemsPerPage);
  const startIndex = (page - 1) * itemsPerPage;
  const endIndex = startIndex + itemsPerPage;
  const currentPageItems = cars.slice(startIndex, endIndex);
  const currentItem = cars[page - 1];


  return( <div> 

        {currentItem && (

<CardCar
key={page}
num={nomer}
img={page === 1 ? car1image || standartCar : standartCar}
wanted={car && typeof car === 'object' ? car.is_stolen : false}
brand={currentItem.vendor || (car && typeof car === 'object' ? car.vendor : '')}
model={currentItem.model || (car && typeof car === 'object' ? car.model : '')}
modelyear={currentItem.model_year || (car && typeof car === 'object' ? car.model_year : '').toString()}
region={currentItem.address || (car && typeof car === 'object' && car.region ? car.region.name : '')}
datereg={currentItem.registered_at || ''}
color={currentItem.color && currentItem.color.ru ? currentItem.color.ru : ''}
fuel={currentItem.fuel && currentItem.fuel.ru ? currentItem.fuel.ru : ''}
/>
      )}

<PaginationControl
  page={page}
  between={4}
  total={cars.length}
  limit={itemsPerPage}
  changePage={(newPage) => setPage(newPage)}
  ellipsis={1}
/>
  </div>




  )

};

export default Pca;
