import React, { FC } from 'react';
import { PcaWrapper } from './pca.styled';
import Pagination from 'react-bootstrap/Pagination';
import CardCar from '../CardCar/CardCar';


interface CarProps {
  car: {
    comments: string[];
    digits: string;
    is_stolen: boolean;
    model: string;
    model_year: number;
    operations: CarData[]; // Массив операций типа CarData
    photo_url: string;
    region: {
      name: string;
      name_ua: string;
      slug: string;
      old_code: string;
      new_code: string;
    };
    stolen_details: any;
    vendor: string;
    vin: string;
  } | null | string;
  nomer?: string; 
}

const Pca: FC<CarProps> = ({ car, nomer = "" }) => {
  let items: JSX.Element[] = [];
  let active = 1;

  if (typeof car === 'object' && car !== null && 'vin' in car && car.operations) {
    // Добавляем первую страницу пагинации
    items.push(
      <Pagination.Item key={1}>
        <CardCar
          num={nomer}
          vin={(car.vin || '').toString()}
          img={car.photo_url || ''}
          wanted={Boolean(car.is_stolen)}
          brand={car.operations[0].vendor || ''} // Пример использования первой операции
          model={car.model || ''}
          modelyear={(car.model_year || '').toString()}
          region={(car.region && car.region.name) || ''}
        />
      </Pagination.Item>
    );

    // Добавляем остальные страницы пагинации
    for (let number = 2; number <= 5; number++) {
      items.push(
        <Pagination.Item key={number}>
          <CardCar
            num={nomer}
            vin={"No VIN"}
            img={car.photo_url || ''}
            wanted={Boolean(car.is_stolen)}
            brand={car.vendor || ''}
            model={car.model || ''}
            modelyear={(car.model_year || '').toString()}
            region={(car.region && car.region.name) || ''}
          />
        </Pagination.Item>
      );
    }
  }

  return (
    <PcaWrapper>
      <div>
        <Pagination size="lg">{items}</Pagination>
        <br />
      </div>
    </PcaWrapper>
  );
};

export default Pca;
