// src/components/SvgMap/SvgMap.tsx
import React from 'react';
import { ReactSVG } from 'react-svg';
import { SvgContainer, Modal, CloseButton } from './SVGMap.styled';
import Map from './ukraine.svg'

interface SvgMapProps {
  isOpen: boolean;
  onClose: () => void;
}

const SvgMap: React.FC<SvgMapProps> = ({ isOpen, onClose }) => {
  if (!isOpen) return null; // Не рендерим ничего, если модальное окно закрыто

  return (
    <Modal>
      <CloseButton onClick={onClose}>Закрыть</CloseButton>
      <SvgContainer>
      <ReactSVG src="ukraine.svg" />
      </SvgContainer>
    </Modal>
  );
};

export default SvgMap;
