import React, { FC, useState } from 'react';
import { PcaWrapper } from './pca.styled';
import Pagination  from 'react-bootstrap/Pagination';
import CardCar from '../CardCar/CardCar';
import { PaginationControl } from 'react-bootstrap-pagination-control';
import standartCarImg from '../../img/standartcar.jpg'


interface CarProps {
  car: {
    comments: string[];
    digits: string;
    is_stolen: boolean;
    model: string;
    model_year: number;
    operations: Array<any>;
    photo_url: string;
    region: {
      name: string;
      name_ua: string;
      slug: string;
      old_code: string;
      new_code: string;
    };
    stolen_details: any;
    vendor: string;
    vin: string;
  } | null | string;
  nomer?: string; 
}

const Pca: FC<CarProps> = ({ car, nomer = "" }) => {
  const [page, setPage] = useState(1);
  const cars = [];


  if (car != null && typeof car === 'object')
  {
    var car1image = car.photo_url;
    var standartCar = standartCarImg;
    for (let i = 0; i<car.operations.length; i++){
      cars.push(car.operations[i]);
    }
  }

  console.log(cars);


  const [currentCar, setCurrentCarPage] = useState(1);
  const recordsPerCard = 1;
  const lastIndex = currentCar * cars.length;
  const firstIndex = lastIndex - recordsPerCard;
  const records = cars.slice(firstIndex, lastIndex);
  const npage = Math.ceil(cars.length / recordsPerCard);
  const numbers = [...Array(npage + 1)];

  return( <div> 

      {cars.map((item, index) => (
         <CardCar
          num={nomer}
          img={index === 0 ? car1image || standartCar : standartCar}         
          wanted={false}
          brand={item.vendor}
          model={item.model}
          modelyear={item.model_year}
          region={item.address}
          datereg={item.registered_at}
          color={item.color.ru}
          fuel={item.fuel.ru}
       />
      ))}

    <PaginationControl
      page={page}
      between={4}
      total={250}
      limit={20}
      changePage={(page) => {
        setPage(page)
      }}
      ellipsis={1}
    />
  </div>




  )

};

export default Pca;
