import React, { FC } from 'react';
import { PcaWrapper } from './pca.styled';
import Pagination from 'react-bootstrap/Pagination';
import CardCar from '../CardCar/CardCar';



interface CarData{
   comments: string[];
   digits: string;
   is_stolen: boolean;
   model: string;
   model_year: number;
   operations: CarData[]; // Массив операций типа CarData
   photo_url: string;
   region: {
     name: string;
     name_ua: string;
     slug: string;
     old_code: string;
     new_code: string;
   };
   stolen_details: any;
   vendor: string;
   vin: string;
 } 

 interface CarProps {
   car: {
     comments: string[];
     digits: string;
     is_stolen: boolean;
     model: string;
     model_year: number;
     operations: CarData[]; // Массив операций типа CarData
     photo_url: string;
     region: {
       name: string;
       name_ua: string;
       slug: string;
       old_code: string;
       new_code: string;
     };
     stolen_details: any;
     vendor: string;
     vin: string;
   } | null | string;
   nomer?: string; 
 }

 const Pca: FC<CarProps> = ({ car, nomer = "" }) => {
   let items: JSX.Element[] = [];

   if (typeof car === 'object' && car !== null && 'vin' in car && car.operations) {
     // Добавляем элементы пагинации для каждой операции автомобиля
     car.operations.forEach((operation, index) => {
       items.push(
         <Pagination.Item key={index + 1}>
           <CardCar
             num={nomer}
             vin={car.vin}
             img={car.photo_url}
             wanted={car.is_stolen}
             brand={operation.vendor} // Используем данные конкретной операции
             model={car.model}
             modelyear={car.model_year.toString()}
             region={car.region.name}
           />
         </Pagination.Item>
       );
     });
   }

   return (
     <PcaWrapper>
       <div>
         <Pagination size="lg">{items}</Pagination>
         <br />
       </div>
     </PcaWrapper>
   );
 };

 export default Pca;
