import styled, { keyframes } from 'styled-components';

const slideInRight = keyframes`
  from {
    opacity: 0;
    transform: translateX(100%);
  }
  to {
    opacity: 1;
    transform: translateX(0);
  }
`;

const slideOutLeft = keyframes`
  from {
    opacity: 1;
    transform: translateX(0);
  }
  to {
    opacity: 0;
    transform: translateX(-100%);
  }
`;

const slideInLeft = keyframes`
  from {
    opacity: 0;
    transform: translateX(-100%);
  }
  to {
    opacity: 1;
    transform: translateX(0);
  }
`;

const slideOutRight = keyframes`
  from {
    opacity: 1;
    transform: translateX(0);
  }
  to {
    opacity: 0;
    transform: translateX(100%);
  }
`;

// Styled components using the keyframes
export const PcaWrapper = styled.div`
  background-color: transparent;
  color: red;
  position: relative;
  height: 300px; // Установите подходящую высоту
`;

export const PaginationControlContainer = styled.div`
  margin-left: 0px;
`;



export const CardCarWrapper = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  will-change: transform;

  &.slide-out-left {
    animation: ${slideOutLeft} 0.3s forwards;
  }
  &.slide-in-right {
    animation: ${slideInRight} 0.3s forwards;
  }
  &.slide-out-right {
    animation: ${slideOutRight} 0.3s forwards;
  }
  &.slide-in-left {
    animation: ${slideInLeft} 0.3s forwards;
  }
`;
