import React, { FC } from 'react';
import { PcaWrapper } from './pca.styled';
import Pagination from 'react-bootstrap/Pagination';
import CardCar from '../CardCar/CardCar';

interface CarProps {
  car: {
    comments: string[];
    digits: string;
    is_stolen: boolean;
    model: string;
    model_year: number;
    operations: Array<{
      is_last: boolean;
      registered_at: string;
      model_year: number;
      vendor: string;
      model: string;
      operation: {
        ru: string;
        ua: string;
      };
      department: string;
      color: {
        ru: string;
        ua: string;
      };
      address: string;
    }>;
    photo_url: string;
    region: {
      name: string;
      name_ua: string;
      slug: string;
      old_code: string;
      new_code: string;
    };
    stolen_details: any;
    vendor: string;
    vin: string;
  } | null | string;
  nomer?: string;
}

const Pca: FC<CarProps> = ({ car, nomer = "" }) => {
  let items = [];

  if (typeof car === 'object' && car !== null && 'operations' in car) {
    items = car.operations.map((operation, index) => (
      <Pagination.Item key={index}>
        <CardCar
          num={nomer}
          vin={(car.vin || '').toString()}
          img={car.photo_url || ''}
          wanted={Boolean(car.is_stolen)}
          brand={operation.vendor || car.vendor || ''}
          model={operation.model || car.model || ''}
          modelyear={(operation.model_year || '').toString()}
          region={operation.address || ''}

        />
      </Pagination.Item>
    ));
  }

  return (
    <PcaWrapper>
      <div>
        <Pagination size="lg">{items}</Pagination>
        <br />
      </div>
    </PcaWrapper>
  );
};

export default Pca;
