import styled from 'styled-components';

export const ButtonWrapper = styled.div`
  position: fixed;
  bottom: 20px;
  left: 20px;
  z-index: 1000;
`;

export const Button = styled.button`
  background-color: #4CAF50;
  border: none;
  color: white;
  padding: 15px 32px;
  text-align: center;
  text-decoration: none;
  display: inline-block;
  font-size: 16px;
  cursor: pointer;
  border-radius: 8px;
  transition: background-color 300ms ease-in-out, opacity 300ms ease-in-out, transform 300ms ease-in-out;

  &:hover {
    background-color: #45a049;
  }

  .fade-enter & {
    opacity: 0;
    transform: translateY(20px);
  }

  .fade-enter-active &,
  .fade-enter-done & {
    opacity: 1;
    transform: translateY(0);
  }

  .fade-exit & {
    opacity: 1;
    transform: translateY(0);
  }

  .fade-exit-active & {
    opacity: 0;
    transform: translateY(20px);
  }
`;
