import React, { FC, useState } from 'react';
import { PcaWrapper } from './pca.styled';
import Pagination from 'react-bootstrap/Pagination';
import CardCar from '../CardCar/CardCar';

interface CarData {
  comments: string[];
  digits: string;
  is_stolen: boolean;
  model: string;
  model_year: number;
  operations: CarData[];
  photo_url: string;
  region: {
    name: string;
    name_ua: string;
    slug: string;
    old_code: string;
    new_code: string;
  };
  stolen_details: any;
  vendor: string;
  vin: string;
}

interface CarProps {
  car: {
    comments: string[];
    digits: string;
    is_stolen: boolean;
    model: string;
    model_year: number;
    operations: CarData[];
    photo_url: string;
    region: {
      name: string;
      name_ua: string;
      slug: string;
      old_code: string;
      new_code: string;
    };
    stolen_details: any;
    vendor: string;
    vin: string;
  } | null | string;
  nomer?: string;
}

const Pca: FC<CarProps> = ({ car, nomer = "" }) => {
  const [activePage, setActivePage] = useState(1);
  const itemsPerPage = 1; // Количество карточек машин на одной странице

  let totalPages = 1;
  let paginatedOperations: CarData[] = [];

  if (typeof car === 'object' && car !== null && 'vin' in car && car.operations) {
    totalPages = Math.ceil(car.operations.length / itemsPerPage);
    const startIndex = (activePage - 1) * itemsPerPage;
    const endIndex = startIndex + itemsPerPage;
    paginatedOperations = car.operations.slice(startIndex, endIndex);
  }

  const handlePageChange = (pageNumber: number) => {
    setActivePage(pageNumber);
  };

  return (
    <PcaWrapper>
      <div>
        <Pagination size="lg">
          {paginatedOperations.map((operation, index) => (
            <Pagination.Item key={index + 1} active={index + 1 === activePage} onClick={() => handlePageChange(index + 1)}>
              <CardCar
                num={nomer}
                vin={car.vin}
                img={car.photo_url}
                wanted={car.is_stolen}
                brand={operation.vendor}
                model={car.model}
                modelyear={car.model_year.toString()}
                region={car.region.name}
              />
            </Pagination.Item>
          ))}
        </Pagination>
        <br />
        <Pagination>
          {Array.from({ length: totalPages }).map((_, index) => (
            <Pagination.Item key={index + 1} active={index + 1 === activePage} onClick={() => handlePageChange(index + 1)}>
              {index + 1}
            </Pagination.Item>
          ))}
        </Pagination>
      </div>
    </PcaWrapper>
  );
};

export default Pca;
